package com.digitale.screens;import com.badlogic.gdx.Gdx;import com.badlogic.gdx.graphics.Color;import com.badlogic.gdx.graphics.g2d.TextureAtlas;import com.badlogic.gdx.graphics.g2d.TextureRegion;import com.badlogic.gdx.scenes.scene2d.ui.ImageButton;import com.badlogic.gdx.scenes.scene2d.ui.Button.ButtonStyle;import com.badlogic.gdx.scenes.scene2d.ui.ImageButton.ImageButtonStyle;import com.badlogic.gdx.scenes.scene2d.utils.TextureRegionDrawable;import com.digitale.myepiclife.MyEpicLife;public class MELButton {	private String TAG="MELBUTTON: ";	int screenX = Gdx.graphics.getWidth();	int screenY = Gdx.graphics.getHeight();	TextureAtlas textureAtlas;	public MELButton() {	}        public ImageButton getButton(String buttonName) throws ButtonNotFoundException{    	 textureAtlas=MyEpicLife.manager.get("data/ui/hifibuttons.atlas");    	TextureRegion image = textureAtlas.findRegion(buttonName+"up");		TextureRegion imagePressed = textureAtlas.findRegion(buttonName+"pressed");		ImageButtonStyle style = new ImageButtonStyle(				MyEpicLife.uiSkin.get(ButtonStyle.class));		style.imageUp = new TextureRegionDrawable(image);		style.imageDown = new TextureRegionDrawable(imagePressed);		ImageButton button = new ImageButton(style);		button.getImageCell().size(screenY / 6, screenY / 6);		button.setColor(MyEpicLife.uiColour);		return button;    	    }    }